h3,l3 = ax3.get_legend_handles_labels()
ax1.legend(h1+h3,l1+l3)

ax1.set_xlabel('episodes')
ax1.set_ylabel('reward')
ax1.set_ylim(-300, 300)
ax1.grid(True)
ax3.set_ylabel('num')
ax3.set_ylim(0.0,18.0)


